import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { QRCodeSVG } from 'qrcode.react';
import SendMoneyForm from '../components/wallet/SendMoneyForm';
import QRScanner from '../components/wallet/QRScanner';
import { getStoredWallet, storeWallet } from '../utils/localStorage';

const Dashboard = () => {
  const [wallet, setWallet] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [qrCode, setQrCode] = useState('');
  const [showQR, setShowQR] = useState(false);
  const [showSendForm, setShowSendForm] = useState(false);
  const [showScanner, setShowScanner] = useState(false);
  const [recipientAddress, setRecipientAddress] = useState('');
  const [isOffline, setIsOffline] = useState(!navigator.onLine);

  // Check online status
  useEffect(() => {
    const handleOnline = () => setIsOffline(false);
    const handleOffline = () => setIsOffline(true);

    window.addEventListener('online', handleOnline);
    window.addEventListener('offline', handleOffline);

    return () => {
      window.removeEventListener('online', handleOnline);
      window.removeEventListener('offline', handleOffline);
    };
  }, []);

  useEffect(() => {
    const getWallet = async () => {
      try {
        // Try to get wallet from API if online
        if (navigator.onLine) {
          const res = await axios.get('/api/wallets');
          const walletData = res.data;
          setWallet(walletData);
          storeWallet(walletData); // Save to localStorage
        } else {
          // Use localStorage data when offline
          const storedWallet = getStoredWallet();
          if (storedWallet) {
            setWallet(storedWallet);
          } else {
            setError('No wallet data available offline');
          }
        }
        setLoading(false);
      } catch (err) {
        console.error('Error loading wallet:', err);
        
        // Try localStorage as fallback
        const storedWallet = getStoredWallet();
        if (storedWallet) {
          setWallet(storedWallet);
        } else {
          setError('Failed to load wallet data');
        }
        setLoading(false);
      }
    };

    getWallet();
  }, []);

  const generateQRCode = async () => {
    try {
      if (navigator.onLine) {
        const res = await axios.get('/api/wallets/generate-qr');
        setQrCode(res.data.qrCode);
      } else {
        // Generate QR client-side when offline
        setQrCode('');
      }
      setShowQR(true);
    } catch (err) {
      setError('Failed to generate QR code');
    }
  };

  const closeQR = () => {
    setShowQR(false);
  };

  const openSendForm = () => {
    setShowSendForm(true);
    setShowScanner(false);
    setShowQR(false);
  };

  const closeSendForm = () => {
    setShowSendForm(false);
    setRecipientAddress('');
  };

  const openScanner = () => {
    if (navigator.mediaDevices && navigator.mediaDevices.getUserMedia) {
      setShowScanner(true);
      setShowSendForm(false);
      setShowQR(false);
    } else {
      setError('Camera access not available on your device');
    }
  };

  const closeScanner = () => {
    setShowScanner(false);
  };

  const handleScanSuccess = (data) => {
    setRecipientAddress(data);
    setShowScanner(false);
    setShowSendForm(true);
  };

  const handleTransactionSuccess = async (updatedWallet) => {
    if (updatedWallet) {
      // If we got an updated wallet directly (offline mode)
      setWallet(updatedWallet);
      storeWallet(updatedWallet);
    } else if (navigator.onLine) {
      // If online, fetch the latest wallet
      try {
        const res = await axios.get('/api/wallets');
        setWallet(res.data);
        storeWallet(res.data);
      } catch (err) {
        setError('Failed to refresh wallet data');
      }
    }
    
    setShowSendForm(false);
    setRecipientAddress('');
  };

  if (loading) {
    return <div>Loading wallet data...</div>;
  }

  if (error) {
    return <div className="alert alert-danger">{error}</div>;
  }

  return (
    <div>
      <h1 className="text-center my-4">Dashboard</h1>
      
      {isOffline && (
        <div className="alert alert-warning">
          <i className="fas fa-wifi-slash"></i> You are currently offline. Some features may be limited.
        </div>
      )}
      
      {wallet && (
        <div className="wallet-card">
          <div className="wallet-header">
            <h2>Your PAY Account</h2>
          </div>
          <div>
            <p>Account ID:</p>
            <div className="wallet-address">{wallet.walletAddress}</div>
          </div>
          <div className="wallet-balance">
            Balance: {wallet.balance} PAY
          </div>
          <div className="wallet-controls">
            <button className="btn btn-primary" onClick={generateQRCode}>
              <i className="fas fa-qrcode"></i> Generate QR Code
            </button>
            <button className="btn btn-secondary" onClick={openSendForm}>
              <i className="fas fa-paper-plane"></i> Send Money
            </button>
            <button 
              className="btn btn-dark" 
              onClick={openScanner}
              disabled={isOffline}
            >
              <i className="fas fa-camera"></i> Scan QR Code
            </button>
          </div>

          {showQR && (
            <div className="qr-container">
              <h3>Your PAY Account QR Code</h3>
              <p>Scan this code to receive money</p>
              {qrCode ? (
                <img src={qrCode} alt="PAY QR Code" />
              ) : (
                <QRCodeSVG value={wallet.walletAddress} size={200} />
              )}
              <button className="btn btn-light mt-3" onClick={closeQR}>
                Close
              </button>
            </div>
          )}

          {showSendForm && (
            <SendMoneyForm 
              onClose={closeSendForm} 
              onSuccess={handleTransactionSuccess} 
              initialRecipientAddress={recipientAddress} 
              wallet={wallet}
              isOffline={isOffline}
            />
          )}

          {showScanner && (
            <QRScanner 
              onClose={closeScanner} 
              onScanSuccess={handleScanSuccess} 
            />
          )}
        </div>
      )}
    </div>
  );
};

export default Dashboard;
