import React, { useState, useEffect } from 'react';
import { Html5QrcodeScanner } from 'html5-qrcode';

const QRScanner = ({ onClose, onScanSuccess }) => {
  const [scanResult, setScanResult] = useState(null);

  useEffect(() => {
    // Create scanner instance
    const scanner = new Html5QrcodeScanner('reader', {
      qrbox: {
        width: 250,
        height: 250
      },
      fps: 10,
      rememberLastUsedCamera: true
    });

    // Success callback
    const onScannerSuccess = (decodedText, decodedResult) => {
      // Stop scanner
      scanner.clear();
      setScanResult(decodedText);
      
      // Call parent's success callback
      if (onScanSuccess) {
        onScanSuccess(decodedText);
      }
    };

    // Error callback
    const onScannerError = (err) => {
      console.warn(err);
    };

    // Render scanner
    scanner.render(onScannerSuccess, onScannerError);

    // Cleanup function
    return () => {
      scanner.clear();
    };
  }, [onScanSuccess]);

  return (
    <div className="qr-scanner my-4">
      <h3>
        <i className="fas fa-camera"></i> Scan QR Code
      </h3>
      <p>Point your camera at a wallet QR code to scan it</p>
      
      {scanResult ? (
        <div className="scan-result">
          <div className="alert alert-success">
            <strong>Scan successful!</strong> Address detected.
          </div>
          <button 
            className="btn btn-primary"
            onClick={onClose}
          >
            Continue
          </button>
        </div>
      ) : (
        <div className="scan-container">
          <div id="reader"></div>
          <button 
            className="btn btn-light mt-3"
            onClick={onClose}
          >
            Cancel
          </button>
        </div>
      )}
    </div>
  );
};

export default QRScanner;
