import React, { useState, useEffect } from 'react';
import { Html5QrcodeScanner } from 'html5-qrcode';

const QRScanner = ({ onClose, onScanSuccess }) => {
  const [scanResult, setScanResult] = useState(null);
  const [hasFlash, setHasFlash] = useState(false);
  const [isFlashOn, setIsFlashOn] = useState(false);
  const [cameras, setCameras] = useState([]);
  const [selectedCamera, setSelectedCamera] = useState(null);
  const [scanner, setScanner] = useState(null);

  useEffect(() => {
    // Check for flash/torch capability
    if (navigator.mediaDevices && navigator.mediaDevices.getSupportedConstraints) {
      const constraints = navigator.mediaDevices.getSupportedConstraints();
      setHasFlash(constraints.torch || false);
    }

    // Get available cameras
    Html5QrcodeScanner.getCameras()
      .then(devices => {
        setCameras(devices);
        if (devices.length > 0) {
          setSelectedCamera(devices[0].id);
        }
      })
      .catch(err => console.error('Error getting cameras:', err));

    // Create scanner instance with advanced config
    const newScanner = new Html5QrcodeScanner('reader', {
      qrbox: {
        width: 250,
        height: 250
      },
      fps: 10,
      rememberLastUsedCamera: true,
      aspectRatio: 1.0,
      showTorchButtonIfSupported: true,
      supportedScanTypes: [
        Html5QrcodeScanner.SCAN_TYPE_CAMERA,
        Html5QrcodeScanner.SCAN_TYPE_FILE
      ]
    });

    setScanner(newScanner);

    // Success callback with vibration
    const onScannerSuccess = (decodedText, decodedResult) => {
      // Vibrate if supported
      if (navigator.vibrate) {
        navigator.vibrate(200);
      }

      // Play success sound
      const audio = new Audio('/scan-success.mp3');
      audio.play().catch(e => console.log('Audio play failed:', e));

      // Stop scanner
      newScanner.clear();
      setScanResult(decodedText);
      
      // Call parent's success callback
      if (onScanSuccess) {
        onScanSuccess(decodedText);
      }
    };

    // Error callback
    const onScannerError = (err) => {
      console.warn(err);
    };

    // Render scanner
    newScanner.render(onScannerSuccess, onScannerError);

    // Cleanup function
    return () => {
      if (newScanner) {
        newScanner.clear();
      }
    };
  }, [onScanSuccess]);

  // Toggle flash/torch
  const toggleFlash = async () => {
    try {
      if (scanner) {
        const track = scanner.getVideoTrack();
        if (track) {
          await track.applyConstraints({
            advanced: [{ torch: !isFlashOn }]
          });
          setIsFlashOn(!isFlashOn);
        }
      }
    } catch (err) {
      console.error('Error toggling flash:', err);
    }
  };

  // Switch camera
  const switchCamera = async (cameraId) => {
    try {
      if (scanner) {
        await scanner.stop();
        await scanner.start(
          { deviceId: { exact: cameraId } },
          { fps: 10 }
        );
        setSelectedCamera(cameraId);
      }
    } catch (err) {
      console.error('Error switching camera:', err);
    }
  };

  return (
    <div className="qr-scanner my-4">
      <div className="scanner-header">
        <h3>
          <i className="fas fa-camera"></i> Scan QR Code
        </h3>
        <div className="scanner-controls">
          {hasFlash && (
            <button 
              className={`btn btn-icon ${isFlashOn ? 'active' : ''}`}
              onClick={toggleFlash}
              title="Toggle Flash"
            >
              <i className={`fas fa-bolt ${isFlashOn ? 'text-warning' : ''}`}></i>
            </button>
          )}
          {cameras.length > 1 && (
            <select 
              className="camera-select"
              value={selectedCamera}
              onChange={(e) => switchCamera(e.target.value)}
            >
              {cameras.map(camera => (
                <option key={camera.id} value={camera.id}>
                  {camera.label}
                </option>
              ))}
            </select>
          )}
        </div>
      </div>
      
      <p className="scanner-instructions">
        Point your camera at a wallet QR code to scan it
      </p>
      
      {scanResult ? (
        <div className="scan-result">
          <div className="alert alert-success">
            <i className="fas fa-check-circle"></i>
            <strong>Scan successful!</strong> Address detected.
          </div>
          <button 
            className="btn btn-primary"
            onClick={onClose}
          >
            Continue
          </button>
        </div>
      ) : (
        <div className="scan-container">
          <div id="reader"></div>
          <button 
            className="btn btn-light mt-3"
            onClick={onClose}
          >
            Cancel
          </button>
        </div>
      )}
    </div>
  );
};

export default QRScanner;
