import React from 'react';

const ThemeCustomizer = ({ themeColor, changeThemeColor, darkMode, toggleDarkMode, onClose }) => {
  const themeOptions = [
    { name: 'Green', value: 'green' },
    { name: 'Blue', value: 'blue' },
    { name: 'Purple', value: 'purple' },
    { name: 'Orange', value: 'orange' },
    { name: 'Red', value: 'red' }
  ];

  return (
    <div className="theme-customizer">
      <div className="theme-customizer-header">
        <h3>Appearance</h3>
        <button className="close-btn" onClick={onClose}>
          <i className="fas fa-times"></i>
        </button>
      </div>
      
      <div className="theme-options">
        <h4>Theme Color</h4>
        <div className="color-options">
          {themeOptions.map(option => (
            <button
              key={option.value}
              className={`color-option ${themeColor === option.value ? 'active' : ''}`}
              data-color={option.value}
              onClick={() => changeThemeColor(option.value)}
              aria-label={`${option.name} theme`}
            >
              <span className="color-preview"></span>
              <span className="color-name">{option.name}</span>
              {themeColor === option.value && (
                <i className="fas fa-check check-icon"></i>
              )}
            </button>
          ))}
        </div>
      </div>
      
      <div className="theme-mode">
        <h4>Mode</h4>
        <div className="mode-switch">
          <button 
            className={`mode-option ${!darkMode ? 'active' : ''}`}
            onClick={() => darkMode && toggleDarkMode()}
          >
            <i className="fas fa-sun"></i>
            <span>Light</span>
          </button>
          <button 
            className={`mode-option ${darkMode ? 'active' : ''}`}
            onClick={() => !darkMode && toggleDarkMode()}
          >
            <i className="fas fa-moon"></i>
            <span>Dark</span>
          </button>
        </div>
      </div>
      
      <div className="theme-customizer-footer">
        <p>Changes are saved automatically</p>
      </div>
    </div>
  );
};

export default ThemeCustomizer;
