import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { getStoredWallet, storeTransaction } from '../utils/localStorage';
import SendForm from '../components/wallet/SendForm';
import QRScanner from '../components/wallet/QRScanner';
import '../styles/SendPage.css';

const SendPage = () => {
  const [showScanner, setShowScanner] = useState(false);
  const [recipient, setRecipient] = useState('');
  const navigate = useNavigate();

  const openScanner = () => {
    setShowScanner(true);
  };

  const closeScanner = () => {
    setShowScanner(false);
  };

  const handleScanSuccess = (data) => {
    setRecipient(data);
    setShowScanner(false);
  };

  const handleSendComplete = () => {
    // Navigate back to dashboard after successful transaction
    navigate('/');
  };

  return (
    <div className="send-page-container">
      <div className="page-header">
        <button className="btn btn-light back-button" onClick={() => navigate('/')}>
          <i className="fas fa-arrow-left"></i>
        </button>
        <h1>Send Money</h1>
      </div>

      {showScanner ? (
        <QRScanner 
          onClose={closeScanner} 
          onScanSuccess={handleScanSuccess}
        />
      ) : (
        <div className="send-content">
          <SendForm 
            initialRecipient={recipient}
            onScanClick={openScanner}
            onComplete={handleSendComplete}
          />
          
          <div className="send-info-section">
            <h3>
              <i className="fas fa-info-circle"></i> Sending Information
            </h3>
            <ul className="info-list">
              <li>Transactions are instant and cannot be reversed</li>
              <li>Double-check recipient addresses before sending</li>
              <li>Scan QR codes for more accurate address entry</li>
            </ul>
          </div>
        </div>
      )}
    </div>
  );
};

export default SendPage;
