import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import './BottomNav.css';

const BottomNav = ({ isAuthenticated }) => {
  const location = useLocation();
  
  if (!isAuthenticated) {
    return null; // Don't show bottom nav for unauthenticated users
  }

  return (
    <nav className="bottom-nav">
      <Link 
        to="/" 
        className={`bottom-nav-item ${location.pathname === '/' ? 'active' : ''}`}
      >
        <i className="fas fa-home"></i>
        <span>Home</span>
      </Link>
      
      <Link 
        to="/history" 
        className={`bottom-nav-item ${location.pathname === '/history' ? 'active' : ''}`}
      >
        <i className="fas fa-history"></i>
        <span>History</span>
      </Link>
      
      <Link 
        to="/profile" 
        className={`bottom-nav-item ${location.pathname === '/profile' ? 'active' : ''}`}
      >
        <i className="fas fa-user"></i>
        <span>Profile</span>
      </Link>
    </nav>
  );
};

export default BottomNav;
