import React, { useState, useEffect } from 'react';
import { Html5QrcodeScanner } from 'html5-qrcode';

const QRScanner = ({ onClose, onScanSuccess, onError }) => {
  const [scanResult, setScanResult] = useState(null);
  const [hasFlash, setHasFlash] = useState(false);
  const [isFlashOn, setIsFlashOn] = useState(false);
  const [cameras, setCameras] = useState([]);
  const [selectedCamera, setSelectedCamera] = useState(null);
  const [scanner, setScanner] = useState(null);
  const [error, setError] = useState(null);
  const [isScanning, setIsScanning] = useState(false);

  useEffect(() => {
    // Check for flash/torch capability
    if (navigator.mediaDevices && navigator.mediaDevices.getSupportedConstraints) {
      const constraints = navigator.mediaDevices.getSupportedConstraints();
      setHasFlash(constraints.torch || false);
    }

    // Request camera permission first
    if (navigator.mediaDevices && navigator.mediaDevices.getUserMedia) {
    navigator.mediaDevices.getUserMedia({ video: true })
      .then(() => {
        // Permission granted, now get cameras
        return Html5QrcodeScanner.getCameras();
      })
      .then(devices => {
        setCameras(devices);
        if (devices.length > 0) {
          setSelectedCamera(devices[0].id);
        }
        initializeScanner();
      })
      .catch(err => {
        console.error('Error accessing camera:', err);
        setError('Could not access camera. Please ensure camera permissions are granted.');
        if (onError) {
          onError('Camera access denied. Please check permissions in your browser settings.');
        }
      });
    } else {
      setError('Your browser does not support camera access. Please try a different browser.');
      if (onError) {
        onError('Your browser does not support camera access. Please try a different browser.');
      }
    }

    return () => {
      // Clean up on unmount
      if (scanner) {
        try {
          scanner.clear();
        } catch (err) {
          console.log('Error clearing scanner on unmount:', err);
        }
      }
    };
  }, []); // Empty dependency array to run only once on mount

  const initializeScanner = () => {
    try {
    // Calculate optimal QR box size - responsive
    const screenWidth = window.innerWidth;
    const qrBoxSize = Math.min(screenWidth * 0.7, 250);

    // Create scanner instance with improved mobile config
    const newScanner = new Html5QrcodeScanner('reader', {
      qrbox: {
        width: qrBoxSize,
        height: qrBoxSize
      },
      fps: 10,
      rememberLastUsedCamera: true,
      aspectRatio: 1.0,
      disableFlip: false, // Allow user to flip image if needed
      showTorchButtonIfSupported: false, // We'll use our own button
      supportedScanTypes: [
        Html5QrcodeScanner.SCAN_TYPE_CAMERA, 
        Html5QrcodeScanner.SCAN_TYPE_FILE
      ],
      formatsToSupport: [
        Html5QrcodeScanner.FORMAT_QR_CODE,
        Html5QrcodeScanner.FORMAT_DATA_MATRIX
      ]
    });

    setScanner(newScanner);
      setIsScanning(true);

    // Success callback with vibration
    const onScannerSuccess = (decodedText, decodedResult) => {
        // Set scanning to false to prevent multiple calls
        if (!isScanning) return;
        setIsScanning(false);
        
      // Vibrate if supported
      if (navigator.vibrate) {
        navigator.vibrate(200);
      }

      // Play success sound
      try {
        const audio = new Audio('/scan-success.mp3');
        audio.play().catch(e => console.log('Audio play failed:', e));
      } catch (e) {
        console.log('Audio not supported');
      }

      // Stop scanner
        try {
      newScanner.clear();
        } catch (e) {
          console.log('Error clearing scanner:', e);
        }
        
      setScanResult(decodedText);
      
      // Call parent's success callback
      if (onScanSuccess) {
        try {
          let parsedData = decodedText;
          // Try to parse as JSON if it looks like JSON
          if (decodedText.startsWith('{') && decodedText.endsWith('}')) {
            try {
              parsedData = JSON.parse(decodedText);
              // Use wallet address if available
              if (parsedData.walletAddress) {
                parsedData = parsedData.walletAddress;
              }
            } catch (e) {
              console.log('Not valid JSON, using as is');
            }
          }
          onScanSuccess(parsedData);
        } catch (err) {
          console.error('Error handling scan data:', err);
          setError('Invalid QR code format. Please try again.');
        }
      }
    };

    // Error callback
    const onScannerError = (err) => {
        console.warn('Scanner error:', err);
      const errorMessage = err.includes('permissions') 
        ? 'Camera access denied. Please check permissions.' 
        : 'Error scanning QR code. Please try again.';
      
      setError(errorMessage);
      
      // Pass error to parent component if provided
      if (onError) {
        onError(errorMessage);
      }
    };

    // Render scanner with a small delay to ensure DOM is ready
    setTimeout(() => {
      try {
        newScanner.render(onScannerSuccess, onScannerError);
      } catch (err) {
        console.error('Error rendering scanner:', err);
        const errorMessage = 'Failed to initialize camera. Please try again.';
        setError(errorMessage);
        
        // Pass error to parent component if provided
        if (onError) {
          onError(errorMessage);
        }
      }
      }, 500); // Increased delay for better reliability

    // Handle window resize for responsive qrbox
    const handleResize = () => {
      if (newScanner) {
          try {
        newScanner.clear();
        setTimeout(() => {
          newScanner.render(onScannerSuccess, onScannerError);
            }, 500);
          } catch (err) {
            console.error('Error handling resize:', err);
          }
      }
    };

    window.addEventListener('resize', handleResize);

      // Return cleanup function
    return () => {
      window.removeEventListener('resize', handleResize);
      if (newScanner) {
        try {
          newScanner.clear();
        } catch (e) {
          console.log('Error clearing scanner:', e);
        }
      }
    };
    } catch (err) {
      console.error('Error initializing scanner:', err);
      setError('Failed to initialize QR scanner. Please try again later.');
      if (onError) {
        onError('Failed to initialize QR scanner. Please try again later.');
      }
    }
  };

  // Toggle flash/torch
  const toggleFlash = async () => {
    try {
      if (scanner && scanner.getHtml5QrcodeScanner()) {
        const html5QrCode = scanner.getHtml5QrcodeScanner().getHtml5QrCode();
        if (html5QrCode) {
          await html5QrCode.applyVideoConstraints({
            advanced: [{ torch: !isFlashOn }]
          });
          setIsFlashOn(!isFlashOn);
        }
      }
    } catch (err) {
      console.error('Error toggling flash:', err);
      // Don't show error to user, just silently fail
    }
  };

  // Switch camera
  const switchCamera = async (cameraId) => {
    try {
      if (scanner && scanner.getHtml5QrcodeScanner()) {
        const html5QrCode = scanner.getHtml5QrcodeScanner().getHtml5QrCode();
        if (html5QrCode) {
          await html5QrCode.stop();
          await html5QrCode.start(
            { deviceId: { exact: cameraId } },
            { fps: 10, qrbox: 250 },
            (decodedText) => {
              scanner.getHtml5QrcodeScanner().onScanSuccess(decodedText);
            },
            (errorMessage) => {
              scanner.getHtml5QrcodeScanner().onScanError(errorMessage);
            }
          );
          setSelectedCamera(cameraId);
        }
      }
    } catch (err) {
      console.error('Error switching camera:', err);
      setError('Unable to switch camera. Please try again.');
    }
  };

  return (
    <div className="qr-scanner my-4">
      <div className="scanner-header">
        <h3>
          <i className="fas fa-camera"></i> Scan QR Code
        </h3>
        <div className="scanner-controls">
          {hasFlash && (
            <button 
              className={`btn btn-icon ${isFlashOn ? 'btn-warning' : 'btn-light'}`}
              onClick={toggleFlash}
              title="Toggle Flash"
            >
              <i className={`fas fa-bolt`}></i>
            </button>
          )}
          {cameras.length > 1 && (
            <select 
              className="camera-select form-control"
              value={selectedCamera || ''}
              onChange={(e) => switchCamera(e.target.value)}
            >
              {cameras.map(camera => (
                <option key={camera.id} value={camera.id}>
                  {camera.label || `Camera ${camera.id.slice(0, 4)}`}
                </option>
              ))}
            </select>
          )}
        </div>
      </div>
      
      <p className="scanner-instructions">
        Point your camera at a wallet QR code to scan it
      </p>
      
      {error && (
        <div className="alert alert-danger">
          <i className="fas fa-exclamation-circle"></i> {error}
          <button 
            className="btn btn-sm btn-light mt-2"
            onClick={() => setError(null)}
          >
            Dismiss
          </button>
        </div>
      )}
      
      {scanResult ? (
        <div className="scan-result">
          <div className="alert alert-success">
            <i className="fas fa-check-circle"></i>
            <strong>Scan successful!</strong> Address detected.
          </div>
          <button 
            className="btn btn-primary"
            onClick={onClose}
          >
            Continue
          </button>
        </div>
      ) : (
        <div className="scan-container">
          <div id="reader" className="reader-container"></div>
          <button 
            className="btn btn-light mt-3"
            onClick={onClose}
          >
            Cancel
          </button>
        </div>
      )}
    </div>
  );
};

export default QRScanner;
