import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import QRScanner from '../components/wallet/QRScanner';
import '../styles/ScanPage.css';

const ScanPage = () => {
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handleClose = () => {
    navigate('/');
  };

  const handleScanSuccess = (data) => {
    // Navigate to send page with the scanned address
    navigate(`/send?address=${encodeURIComponent(data)}`);
  };

  return (
    <div className="scan-page-container">
      <div className="page-header">
        <button className="btn btn-light back-button" onClick={() => navigate('/')}>
          <i className="fas fa-arrow-left"></i>
        </button>
        <h1>Scan QR Code</h1>
      </div>

      <div className="scan-content">
        {error && (
          <div className="alert alert-danger">
            <i className="fas fa-exclamation-circle"></i> {error}
            <button 
              className="btn btn-sm btn-light mt-2"
              onClick={() => setError(null)}
            >
              Dismiss
            </button>
          </div>
        )}
        
        <QRScanner 
          onClose={handleClose} 
          onScanSuccess={handleScanSuccess}
          onError={setError}
        />
        
        <div className="scan-info-section">
          <h3>
            <i className="fas fa-info-circle"></i> Scanning Instructions
          </h3>
          <ul className="info-list">
            <li>Position the QR code within the frame</li>
            <li>Ensure good lighting for better scanning</li>
            <li>Hold your device steady while scanning</li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default ScanPage;
